mapper.update:
update member_product_effect set due_date = DATE_FORMAT(DATE_ADD(NOW(),INTERVAL ceil(product_last_num / eating_time) DAY),'%Y-%m-%d 00:00:00'), updator='SYSTEM' where id in
#{item}
and eating_time > 0;

mapper.select:
select id, member_card memberCard, order_no orderNo, product_code productCode, product_name productName, eating_time eatingTime, product_last_num productLastNum, due_date dueDate, taking_state takingState, taking_effect takingEffect, product_count productCount, one_use_num oneUseNum, one_to_times oneToTimes from member_product_effect where 1=1
and member_card = #{memberCard}
and order_no = #{orderNo}
